import { useState } from "react";
import { useTranslation } from 'react-i18next';

export default function AIHospitalityApp() {
  const { t, i18n } = useTranslation();
  const [region, setRegion] = useState("USA");
  const [timing, setTiming] = useState("");

  const fetchTiming = () => {
    fetch(`http://localhost:8000/api/service-timing?region=${region}`)
      .then(res => res.json())
      .then(data => setTiming(data.timing));
  };

  return (
    <div style={{ padding: '1rem', maxWidth: '400px', margin: '0 auto' }}>
      <h1>{t('title')}</h1>

      <label>{t('region')}:</label>
      <select value={region} onChange={e => setRegion(e.target.value)}>
        <option value="USA">USA</option>
        <option value="Europe">Europe</option>
        <option value="Caribbean">Caribbean</option>
        <option value="Asia">Asia</option>
      </select>

      <div style={{ marginTop: '1rem' }}>
        <button onClick={fetchTiming}>{t('getTiming')}</button>
      </div>

      {timing && <p>{t('recommendedTiming')}: {timing}</p>}

      <div style={{ marginTop: '1rem' }}>
        <select value={i18n.language} onChange={e => i18n.changeLanguage(e.target.value)}>
          <option value="en">English</option>
          <option value="es">Espa√±ol</option>
          <option value="nl">Nederlands</option>
          <option value="pap">Papiamentu</option>
        </select>
      </div>
    </div>
  );
}
