import { useState } from "react";

export default function AIHospitalityApp() {
  const [region, setRegion] = useState("USA");
  const [timing, setTiming] = useState("");

  const fetchTiming = () => {
    fetch(`http://localhost:8000/api/service-timing?region=${region}`)
      .then(res => res.json())
      .then(data => setTiming(data.timing));
  };

  return (
    <div style={{ padding: '1rem', maxWidth: '400px', margin: '0 auto' }}>
      <h1>AI Hospitality Assistant</h1>
      <select value={region} onChange={e => setRegion(e.target.value)}>
        <option value="USA">USA</option>
        <option value="Europe">Europe</option>
        <option value="Caribbean">Caribbean</option>
        <option value="Asia">Asia</option>
      </select>
      <button onClick={fetchTiming} style={{ display: 'block', marginTop: '1rem' }}>
        Get Service Timing
      </button>
      {timing && <p>Recommended Timing: {timing}</p>}
    </div>
  );
}
